// Lookup function to get bitmap for a digit
static inline const unsigned char *getDigitBitmap(char digit)
{
    unsigned char c = (unsigned char)digit;
    switch (c)
    {
    case '0':
        return DIGIT_0_BITMAP;
    case '1':
        return DIGIT_1_BITMAP;
    case '2':
        return DIGIT_2_BITMAP;
    case '3':
        return DIGIT_3_BITMAP;
    case '4':
        return DIGIT_4_BITMAP;
    case '5':
        return DIGIT_5_BITMAP;
    case '6':
        return DIGIT_6_BITMAP;
    case '7':
        return DIGIT_7_BITMAP;
    case '8':
        return DIGIT_8_BITMAP;
    case '9':
        return DIGIT_9_BITMAP;
    case ':':
        return COLON_BITMAP;
    case 0xB0:
        return DEGREE_BITMAP; // UTF-8 degree symbol
    default:
        return DIGIT_0_BITMAP;
    }
}

